 2	// Compile with: g++ -std=c++17 main.cpp -o rawproto
 3	// Run (as root): sudo ./rawproto <interface> <dest-mac>
 4	
 5	#include <arpa/inet.h>
 6	#include <errno.h>
 7	#include <net/if.h>
 8	#include <netinet/ether.h>
 9	#include <sys/ioctl.h>
 10	#include <sys/socket.h>
 11	#include <linux/if_packet.h>
 12	#include <unistd.h>
 13	
 14	#include <array>
 15	#include <cstring>
 16	#include <iostream>
 17	#include <stdexcept>
 18	#include <string>
 19	#include <vector>
 20	
 21	// Custom Ethertype (0x88B5 é um valor não‐atribuído, só para experimentação)
 22	static constexpr uint16_t ETH_P_CUSTOM = 0x88B5;
 23	
 24	// Pacote: 1 byte tipo, 2 bytes tamanho, 4 bytes seq, 2 bytes CRC16
 25	#pragma pack(push, 1)
 26	struct PacketHeader {
 27	 uint8_t type;
 28	 uint16_t length;
 29	 uint32_t seq;
 30	 uint16_t crc16;
 31	};
 32	#pragma pack(pop)
 33	
 34	// CRC16-CCITT simples
 35	uint16_t crc16_ccitt(const uint8_t* data, size_t len) {
 36	 uint16_t crc = 0xFFFF;
 37	 while (len--) {
 38	 crc ^= (uint16_t)(*data++) << 8;
 39	 for (int i = 0; i < 8; i++)
 40	 crc = (crc & 0x8000) ? (crc << 1) ^ 0x1021 : (crc << 1);
 41	 }
 42	 return crc;
 43	}
 44	
 45	// Converte string “AA:BB:CC:DD:EE:FF” em 6 bytes
 46	std::array<uint8_t, 6> parse_mac(const std::string& mac) {
 47	 std::array<uint8_t, 6> b;
 48	 if (std::sscanf(mac.c_str(), "%hhx:%hhx:%hhx:%hhx:%hhx:%hhx",
 49	 &b[0], &b[1], &b[2], &b[3], &b[4], &b[5]) != 6)
 50	 throw std::invalid_argument("Formato MAC inválido");
 51	 return b;
 52	}
 53	
 54	class RawSocket {
 55	public:
 56	 RawSocket(const std::string& ifname) {
 57	 // Cria socket RAW
 58	 fd_ = ::socket(AF_PACKET, SOCK_RAW, htons(ETH_P_CUSTOM));
 59	 if (fd_ < 0) throw std::runtime_error("socket(): " + std::string(strerror(errno)));
 60	
 61	 // Índice da interface
 62	 struct ifreq ifr = {};
 63	 std::strncpy(ifr.ifr_name, ifname.c_str(), IFNAMSIZ - 1);
 64	 if (ioctl(fd_, SIOCGIFINDEX, &ifr) < 0)
 65	 throw std::runtime_error("ioctl(SIOCGIFINDEX): " + std::string(strerror(errno)));
 66	 int ifindex = ifr.ifr_ifindex;
 67	
 68	 // MAC de origem
 69	 if (ioctl(fd_, SIOCGIFHWADDR, &ifr) < 0)
 70	 throw std::runtime_error("ioctl(SIOCGIFHWADDR): " + std::string(strerror(errno)));
 71	 for (int i = 0; i < 6; i++)
 72	 src_mac_[i] = (uint8_t)ifr.ifr_hwaddr.sa_data[i];
 73	
 74	 // Bind na interface
 75	 struct sockaddr_ll addr = {};
 76	 addr.sll_family = AF_PACKET;
 77	 addr.sll_protocol = htons(ETH_P_CUSTOM);
 78	 addr.sll_ifindex = ifindex;
 79	 if (bind(fd_, (struct sockaddr*)&addr, sizeof(addr)) < 0)
 80	 throw std::runtime_error("bind(): " + std::string(strerror(errno)));
 81	 }
 82	
 83	 ~RawSocket() {
 84	 if (fd_ >= 0) ::close(fd_);
 85	 }
 86	
 87	 const std::array<uint8_t,6>& src_mac() const { return src_mac_; }
 88	
 89	 // Envia um frame Ethernet completo (dst_mac + src_mac + ethertype + payload)
 90	 void send_frame(const std::array<uint8_t,6>& dst_mac,
 91	 const uint8_t* data, size_t len) 
 92	 {
 93	 std::vector<uint8_t> frame(14 + len);
 94	 std::memcpy(frame.data() , dst_mac.data(), 6);
 95	 std::memcpy(frame.data() + 6 , src_mac_.data(), 6);
 96	 uint16_t eth = htons(ETH_P_CUSTOM);
 97	 std::memcpy(frame.data() + 12, &eth, 2);
 98	 std::memcpy(frame.data() + 14, data, len);
 99	
 100	 ssize_t sent = ::send(fd_, frame.data(), frame.size(), 0);
 101	 if (sent < 0 || (size_t)sent != frame.size())
 102	 throw std::runtime_error("send(): " + std::string(strerror(errno)));
 103	 }
 104	
 105	 // Recebe bruto (bloca até chegar algo ou erro)
 106	 ssize_t recv_frame(uint8_t* buf, size_t buf_size) {
 107	 return ::recv(fd_, buf, buf_size, 0);
 108	 }
 109	
 110	private:
 111	 int fd_{-1};
 112	 std::array<uint8_t,6> src_mac_{};
 113	};
 114	
 115	int main(int argc, char* argv[]) {
 116	 if (argc < 3) {
 117	 std::cerr << "Uso: " << argv[0] << " <interface> <dest-mac>\n";
 118	 return 1;
 119	 }
 120	
 121	 try {
 122	 RawSocket raw(argv[1]);
 123	 auto dst_mac = parse_mac(argv[2]);
 124	
 125	 // Payload de exemplo
 126	 std::string msg = "Hello from C++17 raw protocol!";
 127	 std::vector<uint8_t> payload(msg.begin(), msg.end());
 128	
 129	 // Monta header
 130	 PacketHeader hdr;
 131	 hdr.type = 1; // 1 = data
 132	 hdr.length = htons((uint16_t)payload.size());
 133	 hdr.seq = htonl(1);
 134	 hdr.crc16 = 0;
 135	
 136	 // Calcula CRC sobre (type|length|seq) + payload
 137	 std::vector<uint8_t> crcbuf(sizeof(hdr) + payload.size());
 138	 std::memcpy(crcbuf.data(), &hdr, sizeof(hdr));
 139	 std::memcpy(crcbuf.data()+sizeof(hdr), payload.data(), payload.size());
 140	 hdr.crc16 = htons(crc16_ccitt(crcbuf.data(), crcbuf.size()));
 141	
 142	 // Serializa header+payload
 143	 std::vector<uint8_t> frame_data(sizeof(hdr) + payload.size());
 144	 std::memcpy(frame_data.data(), &hdr, sizeof(hdr));
 145	 std::memcpy(frame_data.data()+sizeof(hdr), payload.data(), payload.size());
 146	
 147	 // Envia
 148	 raw.send_frame(dst_mac, frame_data.data(), frame_data.size());
 149	 std::cout << "Enviado: \"" << msg << "\"\n";
 150	
 151	 // Recebe e imprime o primeiro frame com nosso Ethertype
 152	 uint8_t buf[1600];
 153	 while (true) {
 154	 ssize_t n = raw.recv_frame(buf, sizeof(buf));
 155	 if (n <= 0) {
 156	 std::cerr << "recv() erro: " << strerror(errno) << "\n";
 157	 break;
 158	 }
 159	 uint16_t eth = ntohs(*reinterpret_cast<uint16_t*>(buf+12));
 160	 if (eth != ETH_P_CUSTOM) continue;
 161	
 162	 auto* rh = reinterpret_cast<PacketHeader*>(buf + 14);
 163	 size_t plen = ntohs(rh->length);
 164	 std::string rdata((char*)(buf+14+sizeof(PacketHeader)), plen);
 165	 std::cout << "Recebido type=" << int(rh->type)
 166	 << " seq=" << ntohl(rh->seq)
 167	 << " data=\"" << rdata << "\"\n";
 168	 break;
 169	 }
 170	 }
 171	 catch (const std::exception& ex) {
 172	 std::cerr << "Erro: " << ex.what() << "\n";
 173	 return 1;
 174	 }
 175	
 176	 return 0;
 177	}
